import React, { useState, useEffect } from 'react';
import axios from 'axios';

interface DishName {
  _id: string;
  name: string;
  type: string;  // 新增类型字段
}

const MenuGeneratorPage: React.FC = () => {
  const [dishNames, setDishNames] = useState<DishName[]>([]);
  const [loading, setLoading] = useState(true);
  const [menuData, setMenuData] = useState<{ [key: string]: { [key: string]: string } }>({
    '周一': { '中午荤菜': '', '中午素菜': '', '下午荤菜': '', '下午素菜': '' },
    '周二': { '中午荤菜': '', '中午素菜': '', '下午荤菜': '', '下午素菜': '' },
    '周三': { '中午荤菜': '', '中午素菜': '', '下午荤菜': '', '下午素菜': '' },
    '周四': { '中午荤菜': '', '中午素菜': '', '下午荤菜': '', '下午素菜': '' },
    '周五': { '中午荤菜': '', '中午素菜': '', '下午荤菜': '', '下午素菜': '' },
    '周六': { '中午荤菜': '', '中午素菜': '', '下午荤菜': '', '下午素菜': '' },
    '周日': { '中午荤菜': '', '中午素菜': '', '下午荤菜': '', '下午素菜': '' }
  });

  // 加载菜品名称数据
  const loadDishNames = async () => {
    try {
      setLoading(true);
      const response = await axios.get('http://localhost:5000/api/dish-names/latest');
      console.log('获取到的菜品名称数据:', response.data);
      if (Array.isArray(response.data)) {
        setDishNames(response.data);
      } else {
        console.error('菜品名称数据格式不正确:', response.data);
        alert('菜品名称数据格式不正确');
      }
    } catch (error) {
      console.error('加载菜品名称失败:', error);
      alert('加载菜品名称失败，请刷新页面重试');
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    loadDishNames();
  }, []);

  // 根据类型获取菜品列表
  const getDishesByType = (type: string) => {
    return dishNames.filter(dish => dish.type === type);
  };

  // 随机选择一个菜品
  const getRandomDish = (type: string) => {
    const typeDishes = getDishesByType(type);
    if (typeDishes.length === 0) {
      console.log(`没有可用的${type}菜品`);
      return '暂无菜品';
    }
    const randomIndex = Math.floor(Math.random() * typeDishes.length);
    const selectedDish = typeDishes[randomIndex].name;
    console.log(`随机选择的${type}菜品:`, selectedDish);
    return selectedDish;
  };

  // 生成菜单
  const generateMenu = () => {
    if (dishNames.length === 0) {
      alert('请先上传菜单数据');
      return;
    }

    console.log('开始生成菜单，当前菜品数量:', dishNames.length);
    const newMenuData = { ...menuData };
    
    Object.keys(newMenuData).forEach(day => {
      // 生成中午的菜品
      newMenuData[day]['中午荤菜'] = getRandomDish('荤菜');
      newMenuData[day]['中午素菜'] = getRandomDish('素菜');
      // 生成下午的菜品
      newMenuData[day]['下午荤菜'] = getRandomDish('荤菜');
      newMenuData[day]['下午素菜'] = getRandomDish('素菜');
    });
    
    console.log('生成的菜单数据:', newMenuData);
    setMenuData(newMenuData);
  };

  return (
    <div className="container">
      <h1>菜单生成器</h1>
      
      <div className="mb-6">
        <button
          onClick={generateMenu}
          className="btn btn-primary"
          disabled={loading || dishNames.length === 0}
        >
          {loading ? '加载中...' : '生成菜单'}
        </button>
        {dishNames.length > 0 && (
          <span style={{ marginLeft: '10px' }}>
            当前可用菜品数量: {dishNames.length}
          </span>
        )}
      </div>

      {loading ? (
        <div>加载中...</div>
      ) : dishNames.length === 0 ? (
        <div>请先上传菜单数据</div>
      ) : (
        <div className="table-container">
          <table className="data-table">
            <thead>
              <tr>
                <th>日期</th>
                <th>中午荤菜</th>
                <th>中午素菜</th>
                <th>下午荤菜</th>
                <th>下午素菜</th>
              </tr>
            </thead>
            <tbody>
              {Object.entries(menuData).map(([day, meals]) => (
                <tr key={day}>
                  <td>{day}</td>
                  <td>{meals['中午荤菜'] || '未生成'}</td>
                  <td>{meals['中午素菜'] || '未生成'}</td>
                  <td>{meals['下午荤菜'] || '未生成'}</td>
                  <td>{meals['下午素菜'] || '未生成'}</td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      )}
    </div>
  );
};

export default MenuGeneratorPage;
