import React, { useState } from 'react';
import axios from 'axios';

const SyncMenuPage: React.FC = () => {
  const [syncStatus, setSyncStatus] = useState<string>('');
  const [isLoading, setIsLoading] = useState(false);

  const handleSync = async () => {
    try {
      setIsLoading(true);
      setSyncStatus('正在同步...');
      
      const response = await axios.post('http://localhost:3001/api/sync-menu');
      
      if (response.data.success) {
        setSyncStatus('同步成功！');
      } else {
        setSyncStatus('同步失败：' + response.data.message);
      }
    } catch (error) {
      console.error('同步失败:', error);
      setSyncStatus('同步失败，请检查服务器状态');
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <div className="container">
      <h1>同步菜单</h1>
      <div className="sync-container">
        <button 
          onClick={handleSync}
          className="btn btn-primary"
          disabled={isLoading}
        >
          {isLoading ? '同步中...' : '同步菜单'}
        </button>
        {syncStatus && (
          <div className={`sync-status ${syncStatus.includes('成功') ? 'success' : 'error'}`}>
            {syncStatus}
          </div>
        )}
      </div>
    </div>
  );
};

export default SyncMenuPage;
