import React, { useState } from 'react';
import axios from 'axios';

interface PriceItem {
  id: string;
  item: string;
  price: number;
  unit: string;
}

const PriceListPage: React.FC = () => {
  const [priceItems, setPriceItems] = useState<PriceItem[]>([]);
  const [isEditing, setIsEditing] = useState(false);
  const [editingItem, setEditingItem] = useState<PriceItem | null>(null);

  const handleFileUpload = async (event: React.ChangeEvent<HTMLInputElement>) => {
    const file = event.target.files?.[0];
    if (!file) return;

    const formData = new FormData();
    formData.append('file', file);

    try {
      const response = await axios.post('http://localhost:5000/api/price-list/upload', formData);
      setPriceItems(response.data);
    } catch (error) {
      console.error('上传失败:', error);
      alert('上传失败，请重试');
    }
  };

  const handleEdit = (item: PriceItem) => {
    setEditingItem(item);
    setIsEditing(true);
  };

  const handleSave = async () => {
    if (!editingItem) return;

    try {
      await axios.put(`http://localhost:5000/api/price-list/${editingItem.id}`, editingItem);
      setPriceItems(priceItems.map(item => 
        item.id === editingItem.id ? editingItem : item
      ));
      setIsEditing(false);
      setEditingItem(null);
    } catch (error) {
      console.error('保存失败:', error);
      alert('保存失败，请重试');
    }
  };

  return (
    <div className="container mx-auto px-4 py-8">
      <h1 className="text-3xl font-bold mb-8">价格清单管理</h1>
      
      <div className="mb-6 space-x-4">
        <label className="bg-blue-500 text-white px-4 py-2 rounded cursor-pointer hover:bg-blue-600">
          重新上传我的价格清单
          <input
            type="file"
            accept=".xlsx,.xls"
            onChange={handleFileUpload}
            className="hidden"
          />
        </label>
        <button
          onClick={() => setIsEditing(!isEditing)}
          className="bg-green-500 text-white px-4 py-2 rounded hover:bg-green-600"
        >
          {isEditing ? '完成编辑' : '编辑当前价格清单'}
        </button>
      </div>

      <div className="overflow-x-auto">
        <table className="min-w-full bg-white border border-gray-300">
          <thead>
            <tr>
              <th className="px-6 py-3 border-b">物品名称</th>
              <th className="px-6 py-3 border-b">价格</th>
              <th className="px-6 py-3 border-b">单位</th>
              {isEditing && <th className="px-6 py-3 border-b">操作</th>}
            </tr>
          </thead>
          <tbody>
            {priceItems.map((item) => (
              <tr key={item.id}>
                <td className="px-6 py-4 border-b">
                  {isEditing ? (
                    <input
                      type="text"
                      value={item.item}
                      onChange={(e) => setPriceItems(priceItems.map(i => 
                        i.id === item.id ? {...i, item: e.target.value} : i
                      ))}
                      className="w-full p-2 border rounded"
                    />
                  ) : (
                    item.item
                  )}
                </td>
                <td className="px-6 py-4 border-b">
                  {isEditing ? (
                    <input
                      type="number"
                      value={item.price}
                      onChange={(e) => setPriceItems(priceItems.map(i => 
                        i.id === item.id ? {...i, price: Number(e.target.value)} : i
                      ))}
                      className="w-full p-2 border rounded"
                    />
                  ) : (
                    item.price
                  )}
                </td>
                <td className="px-6 py-4 border-b">
                  {isEditing ? (
                    <input
                      type="text"
                      value={item.unit}
                      onChange={(e) => setPriceItems(priceItems.map(i => 
                        i.id === item.id ? {...i, unit: e.target.value} : i
                      ))}
                      className="w-full p-2 border rounded"
                    />
                  ) : (
                    item.unit
                  )}
                </td>
                {isEditing && (
                  <td className="px-6 py-4 border-b">
                    <button
                      onClick={() => handleEdit(item)}
                      className="text-blue-500 hover:text-blue-700"
                    >
                      编辑
                    </button>
                  </td>
                )}
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
};

export default PriceListPage;
