import React, { useState, useEffect } from 'react';
import axios from 'axios';

interface DishName {
  _id: string;
  name: string;
}

const MenuGeneratorPage: React.FC = () => {
  const [dishNames, setDishNames] = useState<DishName[]>([]);
  const [loading, setLoading] = useState(true);
  const [menuData, setMenuData] = useState<{ [key: string]: { [key: string]: string } }>({
    '周一': { '中午荤菜': '', '中午素菜': '', '下午荤菜': '', '下午素菜': '' },
    '周二': { '中午荤菜': '', '中午素菜': '', '下午荤菜': '', '下午素菜': '' },
    '周三': { '中午荤菜': '', '中午素菜': '', '下午荤菜': '', '下午素菜': '' },
    '周四': { '中午荤菜': '', '中午素菜': '', '下午荤菜': '', '下午素菜': '' },
    '周五': { '中午荤菜': '', '中午素菜': '', '下午荤菜': '', '下午素菜': '' },
    '周六': { '中午荤菜': '', '中午素菜': '', '下午荤菜': '', '下午素菜': '' },
    '周日': { '中午荤菜': '', '中午素菜': '', '下午荤菜': '', '下午素菜': '' }
  });

  // 加载菜品名称数据
  const loadDishNames = async () => {
    try {
      setLoading(true);
      const response = await axios.get('http://localhost:5000/api/dish-names/latest');
      setDishNames(response.data);
    } catch (error) {
      console.error('加载菜品名称失败:', error);
      alert('加载菜品名称失败，请刷新页面重试');
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    loadDishNames();
  }, []);

  // 随机选择一个菜品
  const getRandomDish = () => {
    if (dishNames.length === 0) return '';
    const randomIndex = Math.floor(Math.random() * dishNames.length);
    return dishNames[randomIndex].name;
  };

  // 生成菜单
  const generateMenu = () => {
    const newMenuData = { ...menuData };
    Object.keys(newMenuData).forEach(day => {
      Object.keys(newMenuData[day]).forEach(meal => {
        newMenuData[day][meal] = getRandomDish();
      });
    });
    setMenuData(newMenuData);
  };

  return (
    <div className="container">
      <h1>菜单生成器</h1>
      
      <div className="mb-6">
        <button
          onClick={generateMenu}
          className="btn btn-primary"
        >
          生成菜单
        </button>
      </div>

      {loading ? (
        <div>加载中...</div>
      ) : (
        <div className="table-container">
          <table className="data-table">
            <thead>
              <tr>
                <th>日期</th>
                <th>中午荤菜</th>
                <th>中午素菜</th>
                <th>下午荤菜</th>
                <th>下午素菜</th>
              </tr>
            </thead>
            <tbody>
              {Object.entries(menuData).map(([day, meals]) => (
                <tr key={day}>
                  <td>{day}</td>
                  <td>{meals['中午荤菜']}</td>
                  <td>{meals['中午素菜']}</td>
                  <td>{meals['下午荤菜']}</td>
                  <td>{meals['下午素菜']}</td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      )}
    </div>
  );
};

export default MenuGeneratorPage;
