import React, { useState, useRef } from 'react';
import { BrowserRouter as Router, Routes, Route, Link } from 'react-router-dom';
import MenuPage from './pages/MenuPage';
import PriceListPage from './pages/PriceListPage';
import html2canvas from 'html2canvas';

// API地址配置
const API_BASE_URL = process.env.REACT_APP_API_URL || 'http://192.168.100.12:5000';

const App: React.FC = () => {
  const [menuData, setMenuData] = useState<any>(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const tableRef = useRef<HTMLTableElement>(null);

  const handleGenerateMenu = async () => {
    setLoading(true);
    setError('');
    setMenuData(null);

    const maxRetries = 3;
    let retryCount = 0;

    const tryRequest = async (): Promise<boolean> => {
      try {
        console.log(`尝试请求 (${retryCount + 1}/${maxRetries}):`, `${API_BASE_URL}/api/generate-menu`);

        // 先检查服务健康状态
        const healthCheck = await fetch(`${API_BASE_URL}/api/health`);
        if (!healthCheck.ok) {
          throw new Error('后端服务不可用');
        }

        const res = await fetch(`${API_BASE_URL}/api/generate-menu`, {
          method: 'GET',
          headers: {
            'Accept': 'application/json',
            'Content-Type': 'application/json',
          },
          mode: 'cors',
          credentials: 'omit'
        });

        console.log('收到响应:', res.status, res.statusText);

        if (!res.ok) {
          const errorText = await res.text();
          console.error('响应错误:', errorText);
          throw new Error(`HTTP error! status: ${res.status}, message: ${errorText}`);
        }

        const data = await res.json();
        console.log('解析响应数据:', data);

        if (data.error) {
          setError(data.error);
        } else {
          setMenuData(data);
        }
        return true;
      } catch (e: any) {
        console.error(`请求失败 (尝试 ${retryCount + 1}/${maxRetries}):`, e);
        if (retryCount < maxRetries - 1) {
          retryCount++;
          console.log(`等待重试... (${retryCount}/${maxRetries})`);
          await new Promise(resolve => setTimeout(resolve, 1000)); // 等待1秒后重试
          return tryRequest();
        }
        setError(`请求后端失败: ${e.message || '未知错误'}`);
        return false;
      }
    };

    try {
      await tryRequest();
    } finally {
      setLoading(false);
    }
  };

  const handleDownloadMenu = async () => {
    if (!menuData) return;

    try {
      const tableElement = document.getElementById('menu-table');
      if (!tableElement) {
        throw new Error('找不到菜单表格元素');
      }

      const canvas = await html2canvas(tableElement, {
        useCORS: true,
        logging: true,
        background: '#ffffff'
      });

      const image = canvas.toDataURL('image/jpeg', 1.0);
      const link = document.createElement('a');
      link.download = `weekly-menu-${new Date().toISOString().split('T')[0]}.jpg`;
      link.href = image;
      link.click();
    } catch (error) {
      console.error('下载菜单失败:', error);
      setError('下载菜单失败，请重试');
    }
  };

  const handleGetLatestMenu = async () => {
    try {
      const res = await fetch(`${API_BASE_URL}/api/menu/latest`);
      const data = await res.json();
      if (data.error) {
        setError(data.error);
      } else {
        setMenuData(data);
      }
    } catch (error) {
      console.error('获取最新菜单失败:', error);
      setError('获取最新菜单失败，请重试');
    }
  };

  const handleSaveMenu = async () => {
    if (!menuData) return;

    try {
      const res = await fetch(`${API_BASE_URL}/api/menu/save`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(menuData),
      });

      const data = await res.json();
      if (data.error) {
        setError(data.error);
      } else {
        alert('菜单保存成功！');
      }
    } catch (error) {
      console.error('保存菜单失败:', error);
      setError('保存菜单失败，请重试');
    }
  };

  // 渲染菜单表格
  const renderMenuTable = () => {
    if (!menuData) return null;
    const days = Object.keys(menuData);
    return (
      <div>
        <table ref={tableRef} border={1} style={{ marginTop: 20, width: '100%', textAlign: 'center', borderCollapse: 'collapse' }}>
          <thead>
            <tr>
              <th style={{ padding: '10px', backgroundColor: '#f0f0f0' }}>日期</th>
              <th style={{ padding: '10px', backgroundColor: '#f0f0f0' }}>中餐-荤菜</th>
              <th style={{ padding: '10px', backgroundColor: '#f0f0f0' }}>中餐-素菜</th>
              <th style={{ padding: '10px', backgroundColor: '#f0f0f0' }}>晚餐-荤菜</th>
              <th style={{ padding: '10px', backgroundColor: '#f0f0f0' }}>晚餐-素菜</th>
            </tr>
          </thead>
          <tbody>
            {days.map(day => (
              <tr key={day}>
                <td style={{ padding: '10px' }}>{day}</td>
                <td style={{ padding: '10px' }}>{menuData[day]['中餐']?.['荤菜']}</td>
                <td style={{ padding: '10px' }}>{menuData[day]['中餐']?.['素菜']}</td>
                <td style={{ padding: '10px' }}>{menuData[day]['晚餐']?.['荤菜']}</td>
                <td style={{ padding: '10px' }}>{menuData[day]['晚餐']?.['素菜']}</td>
              </tr>
            ))}
          </tbody>
        </table>
        {menuData && (
          <button
            className="btn btn-primary"
            style={{ marginTop: '20px' }}
            onClick={handleDownloadMenu}
          >
            下载菜单
          </button>
        )}
      </div>
    );
  };

  return (
    <Router>
      <div className="container">
        <Routes>
          <Route path="/" element={
            <div>
              <h1>什么煮糊了管理系统</h1>
              <div className="mb-6">
                <Link 
                  to="/menu" 
                  className="btn btn-primary"
                >
                  上传我的菜单
                </Link>
                <Link 
                  to="/price-list" 
                  className="btn btn-primary"
                  style={{ marginLeft: '10px' }}
                >
                  上传我的价格清单
                </Link>
                <button
                  className="btn btn-primary"
                  style={{ marginLeft: '10px' }}
                  onClick={handleGenerateMenu}
                  disabled={loading}
                >
                  {loading ? '生成中...' : '生成菜单'}
                </button>
              </div>
              {error && <div style={{ color: 'red', marginTop: 10 }}>{error}</div>}
              {renderMenuTable()}
            </div>
          } />
          <Route path="/menu" element={<MenuPage />} />
          <Route path="/price-list" element={<PriceListPage />} />
        </Routes>
      </div>
    </Router>
  );
};

export default App;
