import React, { useState, useEffect } from 'react';
import axios from 'axios';

interface MenuItem {
  _id: string;
  name: string;
  hard: string;
  description: string;
  material: string;
}

const MenuPage: React.FC = () => {
  const [menuItems, setMenuItems] = useState<MenuItem[]>([]);
  const [isEditing, setIsEditing] = useState(false);
  const [editingItem, setEditingItem] = useState<MenuItem | null>(null);
  const [loading, setLoading] = useState(true);

  // 加载最新数据
  const loadLatestData = async () => {
    try {
      setLoading(true);
      const response = await axios.get('http://localhost:5000/api/menu/latest');
      setMenuItems(response.data);
    } catch (error) {
      console.error('加载数据失败:', error);
      alert('加载数据失败，请刷新页面重试');
    } finally {
      setLoading(false);
    }
  };

  // 组件加载时获取数据
  useEffect(() => {
    loadLatestData();
  }, []);

  const handleFileUpload = async (event: React.ChangeEvent<HTMLInputElement>) => {
    const file = event.target.files?.[0];
    if (!file) return;

    const formData = new FormData();
    formData.append('file', file);

    try {
      const response = await axios.post('http://localhost:5000/api/menu/upload', formData);
      setMenuItems(response.data);
    } catch (error) {
      console.error('上传失败:', error);
      alert('上传失败，请重试');
    }
  };

  const handleEdit = (item: MenuItem) => {
    setEditingItem(item);
    setIsEditing(true);
  };

  const handleSave = async (item: MenuItem) => {
    try {
      // 创建一个不包含 _id 的新对象
      const { _id, ...updateData } = item;
      const response = await axios.put(`http://localhost:5000/api/menu/${_id}`, updateData);
      if (response.data.message === '更新成功') {
        setMenuItems(menuItems.map(i => 
          i._id === item._id ? item : i
        ));
        alert('保存成功');
      } else {
        alert('保存失败：' + response.data.error);
      }
    } catch (error: any) {
      console.error('保存失败:', error);
      alert('保存失败：' + (error.response?.data?.error || error.message));
    }
  };

  return (
    <div className="container mx-auto px-4 py-8">
      <h1 className="text-3xl font-bold mb-8">菜单管理</h1>
      
      <div className="mb-6 space-x-4">
        <label className="bg-blue-500 text-white px-4 py-2 rounded cursor-pointer hover:bg-blue-600">
          重新上传我的菜单
          <input
            type="file"
            accept=".xlsx,.xls"
            onChange={handleFileUpload}
            className="hidden"
          />
        </label>
        <button
          onClick={() => setIsEditing(!isEditing)}
          className="bg-green-500 text-white px-4 py-2 rounded hover:bg-green-600"
        >
          {isEditing ? '完成编辑' : '编辑当前菜单'}
        </button>
      </div>

      {loading ? (
        <div>加载中...</div>
      ) : (
        <div className="overflow-x-auto">
          <table className="min-w-full bg-white border border-gray-300">
            <thead>
              <tr>
                <th className="px-6 py-3 border-b">菜品名称</th>
                <th className="px-6 py-3 border-b">难易程度</th>
                <th className="px-6 py-3 border-b">菜品描述</th>
                <th className="px-6 py-3 border-b">原材料</th>
                {isEditing && <th className="px-6 py-3 border-b">操作</th>}
              </tr>
            </thead>
            <tbody>
              {menuItems.map((item) => (
                <tr key={item._id}>
                  <td className="px-6 py-4 border-b">
                    {isEditing ? (
                      <input
                        type="text"
                        value={item.name}
                        onChange={(e) => setMenuItems(menuItems.map(i => 
                          i._id === item._id ? {...i, name: e.target.value} : i
                        ))}
                        className="w-full p-2 border rounded"
                      />
                    ) : (
                      item.name
                    )}
                  </td>
                  <td className="px-6 py-4 border-b">
                    {isEditing ? (
                      <input
                        type="text"
                        value={item.hard}
                        onChange={(e) => setMenuItems(menuItems.map(i => 
                          i._id === item._id ? {...i, hard: e.target.value} : i
                        ))}
                        className="w-full p-2 border rounded"
                      />
                    ) : (
                      item.hard
                    )}
                  </td>
                  <td className="px-6 py-4 border-b">
                    {isEditing ? (
                      <input
                        type="text"
                        value={item.description}
                        onChange={(e) => setMenuItems(menuItems.map(i => 
                          i._id === item._id ? {...i, description: e.target.value} : i
                        ))}
                        className="w-full p-2 border rounded"
                      />
                    ) : (
                      item.description
                    )}
                  </td>
                  <td className="px-6 py-4 border-b">
                    {isEditing ? (
                      <input
                        type="text"
                        value={item.material}
                        onChange={(e) => setMenuItems(menuItems.map(i => 
                          i._id === item._id ? {...i, material: e.target.value} : i
                        ))}
                        className="w-full p-2 border rounded"
                      />
                    ) : (
                      item.material
                    )}
                  </td>
                  {isEditing && (
                    <td className="px-6 py-4 border-b">
                      <button
                        onClick={() => handleSave(item)}
                        className="text-blue-500 hover:text-blue-700"
                      >
                        保存
                      </button>
                    </td>
                  )}
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      )}
    </div>
  );
};

export default MenuPage;
