import React, { useState } from 'react';
import { BrowserRouter as Router, Routes, Route, Link } from 'react-router-dom';
import { GoogleGenerativeAI } from '@google/generative-ai';
import MenuPage from './pages/MenuPage';
import PriceListPage from './pages/PriceListPage';

const App: React.FC = () => {
  const [geminiResponse, setGeminiResponse] = useState<string>('');
  const [isLoading, setIsLoading] = useState<boolean>(false);
  const [error, setError] = useState<string>('');

  const sleep = (ms: number) => new Promise(resolve => setTimeout(resolve, ms));

  const handleGeminiClick = async () => {
    try {
      setIsLoading(true);
      setError('');
      const genAI = new GoogleGenerativeAI('AIzaSyAUF5IHikcePmIPRGLLBATQZQXxMMGDXmQ');
      const model = genAI.getGenerativeModel({ model: "gemini-pro" });

      let retries = 3;
      let lastError = null;

      while (retries > 0) {
        try {
          const result = await model.generateContent("你是谁");
          const response = await result.response;
          const text = response.text();
          setGeminiResponse(text);
          return; // 成功获取响应，退出函数
        } catch (error: any) {
          lastError = error;
          if (error.message?.includes('429') && retries > 1) {
            // 如果是配额限制错误，等待后重试
            const waitTime = 30000; // 减少等待时间到30秒
            setError(`API 调用次数已达上限，等待 ${waitTime/1000} 秒后重试...`);
            await sleep(waitTime);
            retries--;
            continue;
          }
          throw error; // 其他错误直接抛出
        }
      }

      if (lastError) {
        throw lastError;
      }
    } catch (error: any) {
      console.error('Error calling Gemini API:', error);
      if (error.message?.includes('429')) {
        setError('API 调用次数已达上限，请稍后再试（约30秒后）');
      } else {
        setError('调用API时发生错误，请稍后重试');
      }
      setGeminiResponse('');
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <Router>
      <div className="container">
        <Routes>
          <Route path="/" element={
            <div>
              <h1>什么煮糊了管理系统</h1>
              <div className="mb-6">
                <Link 
                  to="/menu" 
                  className="btn btn-primary"
                >
                  上传我的菜单
                </Link>
                <Link 
                  to="/price-list" 
                  className="btn btn-primary"
                  style={{ marginLeft: '10px' }}
                >
                  上传我的价格清单
                </Link>
                <button 
                  onClick={handleGeminiClick}
                  className="btn btn-primary"
                  style={{ marginLeft: '10px' }}
                  disabled={isLoading}
                >
                  {isLoading ? '加载中...' : '点击生成菜单'}
                </button>
              </div>
              {error && (
                <div className="mt-4 p-4 border rounded bg-red-100 text-red-700">
                  <h3>提示：</h3>
                  <p>{error}</p>
                </div>
              )}
              {geminiResponse && (
                <div className="mt-4 p-4 border rounded">
                  <h3>Gemini 回复：</h3>
                  <p>{geminiResponse}</p>
                </div>
              )}
            </div>
          } />
          <Route path="/menu" element={<MenuPage />} />
          <Route path="/price-list" element={<PriceListPage />} />
        </Routes>
      </div>
    </Router>
  );
};

export default App;
