import React, { useState, useEffect } from 'react';
import axios from 'axios';

interface DishDescription {
  _id: string;
  name: string;
  description: string;
}

const DishDescriptionPage: React.FC = () => {
  const [descriptions, setDescriptions] = useState<DishDescription[]>([]);
  const [loading, setLoading] = useState(true);

  // 加载最新数据
  const loadLatestData = async () => {
    try {
      setLoading(true);
      const response = await axios.get('http://localhost:5000/api/dish-description/latest');
      setDescriptions(response.data);
    } catch (error) {
      console.error('加载数据失败:', error);
      alert('加载数据失败，请刷新页面重试');
    } finally {
      setLoading(false);
    }
  };

  // 组件加载时获取数据
  useEffect(() => {
    loadLatestData();
  }, []);

  return (
    <div className="container">
      <h1>菜品描述</h1>
      
      {loading ? (
        <div>加载中...</div>
      ) : descriptions.length === 0 ? (
        <div className="text-center py-4">暂无菜品描述数据</div>
      ) : (
        <div className="table-container">
          <table className="data-table">
            <thead>
              <tr>
                <th>菜品名称</th>
                <th>菜品描述</th>
              </tr>
            </thead>
            <tbody>
              {descriptions.map((item) => (
                <tr key={item._id}>
                  <td>{item.name}</td>
                  <td>{item.description}</td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      )}
    </div>
  );
};

export default DishDescriptionPage;
